mapper.select:

SELECT *
FROM
    (
        SELECT
            @rownum:=@rownum+1  rnum,
        A.*
        FROM
            sns_comment A,
            (SELECT @ROWNUM := 0) R
        WHERE
            postId=#{postId}
    ) list
WHERE rnum > (#{cri.pageNum} - 1) * #{cri.amount} AND rnum <= #{cri.pageNum} * #{cri.amount};
