mapper.select:
update user
set point = point + 100
where userId = #{userId}

update user
set point = point - 50
where userId = #{userId}

update user
set point = point - 100
where userId = #{userId}

mapper.delete:
delete
from cart
where cartId = #{cartId}
  and userId = #{userId}

mapper.update:
update user
set point=#{point}
where userId = #{userId}

update user
set pwd = #{pwd}
where userId = #{userId}

mapper.select:
select count(*)
from user
where userId = #{userId};


select *
from (
         SELECT @rownum:=@rownum+1  rnum,
        A.*
         FROM
             user A,
             (SELECT @ROWNUM := 0) R
         WHERE
             role = 'user'
           and 1=1
     ) list
WHERE rnum > (#{pageNum} - 1) * #{amount}
  AND rnum <= #{pageNum} * #{amount};

select count(*)
from user
where role = 'user';

mapper.update:
update cart
set count = #{count}
where cartId = #{cartId};
